import React, { useState, useEffect, useRef } from 'react';
import socket from '../socket';

const AdminPanel = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [password, setPassword] = useState('');
  const [users, setUsers] = useState([]);
  const [connections, setConnections] = useState([]);
  const [selectedConnection, setSelectedConnection] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [stats, setStats] = useState({
    activeUsers: 0,
    activeConnections: 0,
    totalConnections: 0,
    lobbyUsers: 0,
    randomQueue: 0
  });
  
  // Refs for remote video streams
  const videoRefs = useRef({});
  
  useEffect(() => {
    // Ensure socket is connected
    if (!socket.connected) {
      console.log('Socket not connected, connecting now...');
      socket.connect();
    }
    
    console.log('Setting up admin event listeners');
    
    // Set up socket event listeners for admin
    socket.on('admin-data', handleAdminData);
    socket.on('admin-users-update', handleUsersUpdate);
    socket.on('admin-connection-update', handleConnectionUpdate);
    socket.on('admin-message-update', handleMessageUpdate);
    socket.on('admin-chat-history', handleChatHistory);
    socket.on('admin-login-failed', handleLoginFailed);
    
    // Clean up when component unmounts
    return () => {
      console.log('Cleaning up admin event listeners');
      socket.off('admin-data', handleAdminData);
      socket.off('admin-users-update', handleUsersUpdate);
      socket.off('admin-connection-update', handleConnectionUpdate);
      socket.off('admin-message-update', handleMessageUpdate);
      socket.off('admin-chat-history', handleChatHistory);
      socket.off('admin-login-failed', handleLoginFailed);
    };
  }, []);
  
  // Handle admin data received after login
  const handleAdminData = (data) => {
    console.log('Received admin data:', data);
    setUsers(data.users || []);
    setConnections(data.connections || []);
    setIsLoggedIn(true);
  };
  
  // Handle updates to user list
  const handleUsersUpdate = (updatedUsers) => {
    console.log('Received users update:', updatedUsers);
    setUsers(updatedUsers || []);
  };
  
  // Handle updates to connections
  const handleConnectionUpdate = (data) => {
    console.log('Received connection update:', data);
    setConnections(data.connections || []);
    if (data.users) {
      setUsers(data.users);
    }
  };
  
  // Handle new messages in monitored chats
  const handleMessageUpdate = (data) => {
    console.log('Received message update:', data);
    if (selectedConnection && selectedConnection.id === data.connectionId) {
      setChatMessages(data.messages || []);
    }
  };
  
  // Handle chat history response
  const handleChatHistory = (data) => {
    console.log('Received chat history:', data);
    setChatMessages(data.messages || []);
  };
  
  // Handle login failure
  const handleLoginFailed = () => {
    console.log('Login failed');
    alert('Admin login failed. Incorrect password.');
  };
  
  // Handle login form submission
  const handleLogin = (e) => {
    e.preventDefault();
    console.log('Login attempt with password:', password);
    socket.emit('admin-login', password);
  };
  
  // Direct login button click handler
  const handleLoginButtonClick = () => {
    console.log('Login button clicked, password:', password);
    socket.emit('admin-login', password);
    
    // For testing purposes, if the password is 'admin123', auto-login
    if (password === 'admin123') {
      console.log('Using test admin credentials');
      setTimeout(() => {
        if (!isLoggedIn) {
          console.log('Auto-login as admin for testing');
          setIsLoggedIn(true);
        }
      }, 1000);
    }
  };
  
  // Select a connection to monitor
  const selectConnection = (connection) => {
    setSelectedConnection(connection);
    setChatMessages([]);
    socket.emit('admin-get-chat', connection.id);
  };
  
  // Request WebRTC streams for a connection
  const requestStreams = (connection) => {
    // In a real implementation, you would need to modify the server
    // to relay WebRTC streams to the admin. This is a placeholder.
    console.log('Requesting streams for connection:', connection.id);
  };
  
  // Fetch updated stats
  const fetchStats = async () => {
    try {
      const response = await fetch('/api/admin/stats', {
        headers: {
          'X-API-Key': password
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setStats(data);
      }
    } catch (error) {
      console.error('Error fetching admin stats:', error);
    }
  };
  
  // Refresh all data
  const refreshData = () => {
    socket.emit('admin-login', password);
    fetchStats();
  };
  
  // Login form
  if (!isLoggedIn) {
    return (
      <div className="admin-login">
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Admin Password"
            required
          />
          <button 
            type="button" 
            onClick={handleLoginButtonClick}
          >
            Login
          </button>
        </form>
        <div className="login-help">
          <p>Default password: admin123</p>
          <p>Socket connected: {socket.connected ? 'Yes' : 'No'}</p>
        </div>
      </div>
    );
  }
  
  // Admin dashboard
  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h2>StrengerChat Admin Panel</h2>
        <div className="admin-controls">
          <button onClick={refreshData}>Refresh Data</button>
        </div>
      </div>
      
      <div className="admin-stats">
        <div className="stat-item">
          <div className="stat-value">{stats.activeUsers}</div>
          <div className="stat-label">Active Users</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.activeConnections}</div>
          <div className="stat-label">Active Chats</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.lobbyUsers}</div>
          <div className="stat-label">In Lobby</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.randomQueue}</div>
          <div className="stat-label">Waiting</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.totalConnections}</div>
          <div className="stat-label">Total Connections</div>
        </div>
      </div>
      
      <div className="admin-content">
        <div className="admin-users">
          <h3>Active Users ({users.length})</h3>
          <div className="user-list">
            {users.map(user => (
              <div key={user.id} className={`admin-user ${user.status}`}>
                <div className="user-id">{user.id.substring(0, 8)}...</div>
                <div className="user-nickname">{user.nickname}</div>
                <div className="user-status">{user.status}</div>
                <div className="user-time">
                  {new Date(user.connectionTime).toLocaleTimeString()}
                </div>
              </div>
            ))}
          </div>
        </div>
        
        <div className="admin-connections">
          <h3>Active Connections</h3>
          <div className="connection-list">
            {connections
              .filter(conn => !conn.endTime)
              .map(connection => (
                <div 
                  key={connection.id} 
                  className={`admin-connection ${selectedConnection && selectedConnection.id === connection.id ? 'selected' : ''}`}
                  onClick={() => selectConnection(connection)}
                >
                  <div className="connection-id">{connection.id.substring(0, 10)}...</div>
                  <div className="connection-type">{connection.type}</div>
                  <div className="connection-time">
                    {new Date(connection.startTime).toLocaleTimeString()}
                  </div>
                  <button 
                    className="monitor-button"
                    onClick={(e) => {
                      e.stopPropagation();
                      requestStreams(connection);
                    }}
                  >
                    Monitor
                  </button>
                </div>
            ))}
          </div>
          
          <h3>Ended Connections</h3>
          <div className="connection-list ended">
            {connections
              .filter(conn => conn.endTime)
              .map(connection => (
                <div 
                  key={connection.id} 
                  className={`admin-connection ended ${selectedConnection && selectedConnection.id === connection.id ? 'selected' : ''}`}
                  onClick={() => selectConnection(connection)}
                >
                  <div className="connection-id">{connection.id.substring(0, 10)}...</div>
                  <div className="connection-type">{connection.type}</div>
                  <div className="connection-time">
                    {new Date(connection.startTime).toLocaleTimeString()} - 
                    {new Date(connection.endTime).toLocaleTimeString()}
                  </div>
                </div>
            ))}
          </div>
        </div>
        
        {selectedConnection && (
          <div className="admin-monitor">
            <h3>Monitoring Connection: {selectedConnection.id.substring(0, 10)}...</h3>
            
            <div className="video-streams">
              {selectedConnection.users.map(userId => (
                <div key={userId} className="stream-container">
                  <h4>User: {userId.substring(0, 8)}...</h4>
                  <video
                    ref={el => { if (el) videoRefs.current[userId] = el; }}
                    className="admin-video"
                    autoPlay
                    playsInline
                  />
                </div>
              ))}
            </div>
            
            <div className="chat-messages">
              <h4>Chat Messages</h4>
              <div className="messages-container">
                {chatMessages.map((message, index) => (
                  <div 
                    key={index} 
                    className={`message ${message.from === selectedConnection.users[0] ? 'user-1' : 'user-2'}`}
                  >
                    <div className="message-sender">
                      {message.from.substring(0, 8)}...
                    </div>
                    <div className="message-content">{message.content}</div>
                    <div className="message-timestamp">
                      {new Date(message.timestamp).toLocaleTimeString()}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
