import React, { useState, useEffect, useRef } from 'react';
import socket from '../socket';

const AdminPanel = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [password, setPassword] = useState('');
  const [users, setUsers] = useState([]);
  const [connections, setConnections] = useState([]);
  const [selectedConnection, setSelectedConnection] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [stats, setStats] = useState({
    activeUsers: 0,
    activeConnections: 0,
    totalConnections: 0,
    lobbyUsers: 0,
    randomQueue: 0
  });
  
  // Refs for remote video streams
  const videoRefs = useRef({});
  // Store peer connections for admin monitoring
  const peerConnectionsRef = useRef({});
  
  useEffect(() => {
    // Ensure socket is connected
    if (!socket.connected) {
      console.log('Socket not connected, connecting now...');
      socket.connect();
    }
    
    console.log('Setting up admin event listeners');
    
    // Set up socket event listeners for admin
    socket.on('admin-data', handleAdminData);
    socket.on('admin-users-update', handleUsersUpdate);
    socket.on('admin-connection-update', handleConnectionUpdate);
    socket.on('admin-message-update', handleMessageUpdate);
    socket.on('admin-chat-history', handleChatHistory);
    socket.on('admin-login-failed', handleLoginFailed);
    socket.on('admin-monitor-stream', handleMonitorStream);
    socket.on('admin-webrtc-update', handleWebRTCUpdate);
    socket.on('admin-ice-candidate', handleIceCandidate);
    socket.on('admin-monitor-error', handleMonitorError);
    
    // Clean up when component unmounts
    return () => {
      console.log('Cleaning up admin event listeners');
      socket.off('admin-data', handleAdminData);
      socket.off('admin-users-update', handleUsersUpdate);
      socket.off('admin-connection-update', handleConnectionUpdate);
      socket.off('admin-message-update', handleMessageUpdate);
      socket.off('admin-chat-history', handleChatHistory);
      socket.off('admin-login-failed', handleLoginFailed);
      socket.off('admin-monitor-stream', handleMonitorStream);
      socket.off('admin-webrtc-update', handleWebRTCUpdate);
      socket.off('admin-ice-candidate', handleIceCandidate);
      socket.off('admin-monitor-error', handleMonitorError);
      
      // Close all peer connections
      Object.values(peerConnectionsRef.current).forEach(pc => {
        if (pc) pc.close();
      });
    };
  }, []);
  
  // Handle admin data received after login
  const handleAdminData = (data) => {
    console.log('Received admin data:', data);
    setUsers(data.users || []);
    setConnections(data.connections || []);
    setIsLoggedIn(true);
    
    // Update stats
    setStats({
      activeUsers: (data.users || []).filter(user => user.status !== 'idle').length,
      activeConnections: (data.connections || []).filter(conn => !conn.endTime).length,
      totalConnections: (data.connections || []).length,
      lobbyUsers: (data.lobbyUsers || []).length,
      randomQueue: (data.randomQueue || []).length
    });
  };
  
  // Handle updates to user list
  const handleUsersUpdate = (updatedUsers) => {
    console.log('Received users update:', updatedUsers);
    setUsers(updatedUsers || []);
    
    // Update active users stat
    setStats(prevStats => ({
      ...prevStats,
      activeUsers: (updatedUsers || []).filter(user => user.status !== 'idle').length
    }));
  };
  
  // Handle updates to connections
  const handleConnectionUpdate = (data) => {
    console.log('Received connection update:', data);
    const updatedConnections = data.connections || [];
    setConnections(updatedConnections);
    
    // If the currently selected connection has ended, deselect it
    if (selectedConnection && 
        updatedConnections.find(c => c.id === selectedConnection.id && c.endTime)) {
      setSelectedConnection(null);
      setChatMessages([]);
      
      // Close peer connections for this ended connection
      if (selectedConnection.users) {
        selectedConnection.users.forEach(userId => {
          if (peerConnectionsRef.current[userId]) {
            peerConnectionsRef.current[userId].close();
            delete peerConnectionsRef.current[userId];
          }
        });
      }
    }
    
    if (data.users) {
      setUsers(data.users);
    }
    
    // Update connection stats
    setStats(prevStats => ({
      ...prevStats,
      activeConnections: updatedConnections.filter(conn => !conn.endTime).length,
      totalConnections: updatedConnections.length
    }));
  };
  
  // Handle new messages in monitored chats
  const handleMessageUpdate = (data) => {
    console.log('Received message update:', data);
    if (selectedConnection && selectedConnection.id === data.connectionId) {
      setChatMessages(data.messages || []);
    }
  };
  
  // Handle chat history response
  const handleChatHistory = (data) => {
    console.log('Received chat history:', data);
    setChatMessages(data.messages || []);
  };
  
  // Handle login failure
  const handleLoginFailed = () => {
    console.log('Login failed');
    alert('Admin login failed. Incorrect password.');
  };
  
  // Handle WebRTC updates from users
  const handleWebRTCUpdate = (data) => {
    console.log('Received WebRTC update:', data);
    if (selectedConnection && selectedConnection.id === data.connectionId) {
      // If we're monitoring this connection, request the streams
      requestStreams(selectedConnection);
    }
  };
  
  // Handle monitor stream from user
  const handleMonitorStream = async (data) => {
    console.log('Received monitor stream:', data);
    
    try {
      // Create a connection ID that includes whether this is a remote stream
      const connectionId = data.isRemoteStream ? `${data.userId}-remote` : data.userId;
      
      // Create a new RTCPeerConnection if it doesn't exist
      if (!peerConnectionsRef.current[connectionId]) {
        const iceServers = {
          iceServers: [
            { urls: 'stun:stun.l.google.com:19302' },
            { urls: 'stun:stun1.l.google.com:19302' },
            { urls: 'stun:stun2.l.google.com:19302' },
          ],
        };
        
        const pc = new RTCPeerConnection(iceServers);
        peerConnectionsRef.current[connectionId] = pc;
        
        // Handle ICE candidates
        pc.onicecandidate = (event) => {
          if (event.candidate) {
            console.log('Admin ICE candidate generated:', event.candidate);
            socket.emit('admin-ice-candidate', {
              target: data.userId,
              candidate: event.candidate,
              isRemoteStream: data.isRemoteStream
            });
          }
        };
        
        // Handle incoming tracks
        pc.ontrack = (event) => {
          console.log('Admin received track from user:', connectionId, event);
          
          // Determine which video element to use
          // If it's a remote stream, use the partner's video element
          const videoUserId = data.isRemoteStream ? 
            selectedConnection.users.find(id => id !== data.userId) : 
            data.userId;
          
          if (videoRefs.current[videoUserId]) {
            const videoElement = videoRefs.current[videoUserId];
            videoElement.srcObject = event.streams[0];
            
            // Add event listener to check if video is actually playing
            videoElement.onloadedmetadata = () => {
              console.log('Admin video metadata loaded, playing video');
              videoElement.play()
                .catch(e => console.error('Error playing admin video:', e));
            };
          }
        };
      }
      
      const pc = peerConnectionsRef.current[connectionId];
      
      // Set remote description (the offer from the user)
      await pc.setRemoteDescription(new RTCSessionDescription(data.offer));
      
      // Create answer
      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);
      
      // Send answer to the user
      socket.emit('admin-stream-answer', {
        userId: data.userId,
        answer: answer,
        isRemoteStream: data.isRemoteStream
      });
      
      console.log('Sent stream answer to user:', data.userId, 'isRemoteStream:', data.isRemoteStream);
    } catch (error) {
      console.error('Error handling monitor stream:', error);
    }
  };
  
  // Handle ICE candidate from user
  const handleIceCandidate = async (data) => {
    try {
      console.log('ICE candidate received from:', data.from);
      
      // Determine which connection to use
      const connectionId = data.isRemoteStream ? `${data.from}-remote` : data.from;
      const pc = peerConnectionsRef.current[connectionId];
      
      if (pc) {
        await pc.addIceCandidate(new RTCIceCandidate(data.candidate));
        console.log('ICE candidate added for connection:', connectionId);
      } else {
        console.error('No peer connection found for:', connectionId);
      }
    } catch (error) {
      console.error('Error handling ICE candidate:', error);
    }
  };
  
  // Handle monitor error
  const handleMonitorError = (data) => {
    console.error('Monitor error:', data.error);
    alert(`Error monitoring connection: ${data.error}`);
  };
  
  // Handle login form submission
  const handleLogin = (e) => {
    e.preventDefault();
    console.log('Login attempt with password:', password);
    socket.emit('admin-login', password);
  };
  
  // Direct login button click handler
  const handleLoginButtonClick = () => {
    console.log('Login button clicked, password:', password);
    socket.emit('admin-login', password);
  };
  
  // Select a connection to monitor
  const selectConnection = (connection) => {
    // Clean up previous peer connections if selecting a different connection
    if (selectedConnection && selectedConnection.id !== connection.id) {
      selectedConnection.users.forEach(userId => {
        if (peerConnectionsRef.current[userId]) {
          peerConnectionsRef.current[userId].close();
          delete peerConnectionsRef.current[userId];
        }
      });
    }
    
    setSelectedConnection(connection);
    setChatMessages([]);
    socket.emit('admin-get-chat', connection.id);
    
    // Request video streams immediately when selecting a connection
    requestStreams(connection);
  };
  
  // Request WebRTC streams for a connection
  const requestStreams = (connection) => {
    console.log('Requesting streams for connection:', connection.id);
    socket.emit('admin-monitor-streams', connection.id);
  };
  
  // Fetch updated stats
  const fetchStats = async () => {
    try {
      const response = await fetch('/api/admin/stats', {
        headers: {
          'X-API-Key': password
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setStats(data);
      }
    } catch (error) {
      console.error('Error fetching admin stats:', error);
    }
  };
  
  // Refresh all data
  const refreshData = () => {
    socket.emit('admin-login', password);
    fetchStats();
    
    // If a connection is selected, request streams again
    if (selectedConnection) {
      requestStreams(selectedConnection);
    }
  };
  
  // Get user nickname by ID
  const getUserNickname = (userId) => {
    const user = users.find(u => u.id === userId);
    return user ? user.nickname || 'Anonymous' : 'Unknown';
  };
  
  // Format time duration
  const formatDuration = (startTime, endTime) => {
    const start = new Date(startTime);
    const end = endTime ? new Date(endTime) : new Date();
    const diffMs = end - start;
    const diffMins = Math.floor(diffMs / 60000);
    const diffSecs = Math.floor((diffMs % 60000) / 1000);
    return `${diffMins}m ${diffSecs}s`;
  };
  
  // Login form
  if (!isLoggedIn) {
    return (
      <div className="admin-login">
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Admin Password"
            required
          />
          <button 
            type="button" 
            onClick={handleLoginButtonClick}
          >
            Login
          </button>
        </form>
        <div className="login-help">
          <p>Default password: admin123</p>
          <p>Socket connected: {socket.connected ? 'Yes' : 'No'}</p>
        </div>
      </div>
    );
  }
  
  // Filter active connections (no endTime)
  const activeConnectionsList = connections.filter(conn => !conn.endTime);
  
  // Admin dashboard
  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h2>StrengerChat Admin Panel</h2>
        <div className="admin-controls">
          <button onClick={refreshData}>Refresh Data</button>
        </div>
      </div>
      
      <div className="admin-stats">
        <div className="stat-item">
          <div className="stat-value">{stats.activeUsers}</div>
          <div className="stat-label">Active Users</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.activeConnections}</div>
          <div className="stat-label">Active Chats</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.lobbyUsers}</div>
          <div className="stat-label">In Lobby</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.randomQueue}</div>
          <div className="stat-label">Waiting</div>
        </div>
        <div className="stat-item">
          <div className="stat-value">{stats.totalConnections}</div>
          <div className="stat-label">Total Connections</div>
        </div>
      </div>
      
      <div className="admin-content">
        <div className="admin-users">
          <h3>Active Users ({users.filter(user => user.status !== 'idle').length})</h3>
          <div className="user-list">
            {users
              .filter(user => user.status !== 'idle')
              .map(user => (
                <div key={user.id} className={`admin-user ${user.status}`}>
                  <div className="user-id">{user.id.substring(0, 8)}</div>
                  <div className="user-nickname">{user.nickname || 'Anonymous'}</div>
                  <div className="user-status">
                    <span className={`status-indicator ${user.status}`}></span>
                    {user.status}
                  </div>
                  <div className="user-time">
                    {new Date(user.connectionTime).toLocaleTimeString()}
                  </div>
                </div>
              ))}
          </div>
        </div>
        
        <div className="admin-connections">
          <h3>Active Connections ({activeConnectionsList.length})</h3>
          <div className="connection-list">
            {activeConnectionsList.map(connection => (
              <div 
                key={connection.id} 
                className={`admin-connection ${selectedConnection && selectedConnection.id === connection.id ? 'selected' : ''}`}
                onClick={() => selectConnection(connection)}
              >
                <div className="connection-details">
                  <div className="connection-users">
                    {connection.users.map(userId => (
                      <span key={userId} className="connection-user">
                        {getUserNickname(userId)}
                      </span>
                    )).reduce((prev, curr) => [prev, ' ↔ ', curr])}
                  </div>
                  <div className="connection-type">{connection.type}</div>
                  <div className="connection-time">
                    Started: {new Date(connection.startTime).toLocaleTimeString()}
                    <br />
                    Duration: {formatDuration(connection.startTime)}
                  </div>
                </div>
                <button 
                  className="monitor-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    requestStreams(connection);
                  }}
                >
                  Monitor
                </button>
              </div>
            ))}
            {activeConnectionsList.length === 0 && (
              <div className="no-connections">No active connections</div>
            )}
          </div>
        </div>
        
        {selectedConnection && (
          <div className="admin-monitor">
            <h3>Monitoring Connection</h3>
            <div className="monitor-header">
              <div className="connection-id">ID: {selectedConnection.id.substring(0, 10)}</div>
              <div className="connection-type">Type: {selectedConnection.type}</div>
              <button 
                className="refresh-streams-button"
                onClick={() => requestStreams(selectedConnection)}
              >
                Refresh Streams
              </button>
            </div>
            
            <div className="video-streams">
              {selectedConnection.users.map(userId => (
                <div key={userId} className="stream-container">
                  <h4>{getUserNickname(userId)}</h4>
                  <video
                    ref={el => { if (el) videoRefs.current[userId] = el; }}
                    className="admin-video"
                    autoPlay
                    playsInline
                  />
                  <div className="video-status">
                    {!videoRefs.current[userId]?.srcObject && 
                      <span className="loading-video">Loading video stream...</span>
                    }
                  </div>
                </div>
              ))}
            </div>
            
            <div className="chat-messages">
              <h4>Chat Messages</h4>
              <div className="messages-container">
                {chatMessages.length === 0 ? (
                  <div className="no-messages">No messages yet</div>
                ) : (
                  chatMessages.map((message, index) => {
                    const isUser1 = message.from === selectedConnection.users[0];
                    const sender = isUser1 ? getUserNickname(selectedConnection.users[0]) : getUserNickname(selectedConnection.users[1]);
                    
                    return (
                      <div 
                        key={index} 
                        className={`message ${isUser1 ? 'user-1' : 'user-2'}`}
                      >
                        <div className="message-sender">
                          {sender}
                        </div>
                        <div className="message-content">{message.content}</div>
                        <div className="message-timestamp">
                          {new Date(message.timestamp).toLocaleTimeString()}
                        </div>
                      </div>
                    );
                  })
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
